import socket 
import threading 
import time 

class Worm(object): 
    def __init__(self): 
        self.connections = [] 
        self.payload = "Hello World!" 

    def run(self): 
        while True: 
            for connection in self.connections: 
                connection.sendall(self.payload) 
            time.sleep(5) 

def scan_network(target): 
    print("Scanning network...") 
    with socket.socket(socket.AF_INET, socket.SOCK_STREAM) as s: 
        try: 
            s.connect((target, 80)) 
            print("Target is online.") 
            return True 
        except ConnectionRefusedError: 
            print("Target is offline.") 
            return False 

def main(target): 
    if scan_network(target): 
        print("Connecting to target...") 
        with socket.socket(socket.AF_INET, socket.SOCK_STREAM) as s: 
            try: 
                s.connect((target, 80)) 
                print("Connected.") 
                worm = Worm() 
                t = threading.Thread(target=worm.run, args=(s,)) 
                t.start() 
                worm.connections.append(s) 
                while True: 
                    data = s.recv(1024).decode("utf-8") 
                    if not data: break 
                    print(data) 
            except ConnectionRefusedError: 
                print("Target is offline.") 
                return False 

if __name__ == "__main__": 
    main("target.com")
