import os
import sys
import signal
import multiprocessing

def spawn_process():
    while True:
        try:
            pid = os.fork()
            if pid == 0:
                # Child process
                print(f"Spawned child process {os.getpid()}")
                spawn_process()
            else:
                # Parent process
                print(f"Spawned child process {pid}")
        except OSError as e:
            print(f"Error spawning process: {e}")

if __name__ == "__main__":
    try:
        while True:
            multiprocessing.Process(target=spawn_process).start()
    except KeyboardInterrupt:
        print("Stopping...")
    except Exception as e:
        print(f"Error: {e}")
    finally:
        os.killpg(os.getpgrp(), signal.SIGTERM)
