import subprocess
import re
import base64
from Crypto.Cipher import AES

def get_wifi_passwords():
    data = subprocess.check_output(['netsh', 'wlan', 'show', 'profiles']).decode('utf-8').split('\n')
    profiles = [i.split(":")[1].strip() for i in data if "All User Profile" in i]
    wifi_list = list()

    for i in profiles:
        results = subprocess.check_output(['netsh', 'wlan', 'show', 'profile', i, 'key=clear']).decode('utf-8').split('\n')
        results = [b.split(":")[1].strip() for b in results if "Key Content" in b]

        try:
            wifi_list.append({'ssid': i, 'password': results[0]})
        except IndexError:
            wifi_list.append({'ssid': i, 'password': 'Cannot read!'})

    return wifi_list

def decrypt_wifi_password(encrypted_data):
    key = b'8f3d9a67-90e5-4ab6-b15c-1b9f9c2c12345678'
    cipher = AES.new(key, AES.MODE_ECB)
    decrypted_data = cipher.decrypt(base64.b64decode(encrypted_data))
    return decrypted_data.decode('utf-8').strip()

def main():
    wifi_passwords = get_wifi_passwords()

    for wifi in wifi_passwords:
        if 'Cannot read!' not in wifi['password']:
            try:
                decrypted_password = decrypt_wifi_password(wifi['password'])
                print(f"SSID: {wifi['ssid']}, Password: {decrypted_password}")
            except Exception as e:
                print(f"Error decrypting password for SSID: {wifi['ssid']}. Error: {str(e)}")
        else:
            print(f"SSID: {wifi['ssid']}, Password: Cannot read!")

if __name__ == "__main__":
    main()
