import subprocess
from functools import wraps

def bind_to_url(url):
    def decorator(func):
        @wraps(func)
        def wrapper(*args, **kwargs):
            return func(url, *args, **kwargs)
        return wrapper
    return decorator

@bind_to_url('http://example.com')
def ping(url):
    command = ['ping', '-c', '1', url]
    try:
        pong = subprocess.check_output(command).decode('utf-8').strip()
        if '0% packet loss' in pong and 'time=' in pong:
            return True
        else:
            return False
    except Exception as e:
        print(f"Error: {e}")
        return False

if __name__ == "__main__":
    url = "http://example.com"
    is_alive = ping(url)
    print(f"{url} is {'alive' if is_alive else 'dead'}")
